mapper.select:
SELECT COUNT(DISTINCT group_id, pic_name)
FROM ${tableName}
WHERE
is_delete = 0

    AND group_id = #{qry.groupId}


    AND pic_name LIKE CONCAT('%', #{qry.picName}, '%')

SELECT t.group_id AS groupId, t.pic_name AS groupName, ( SELECT p.pic_url FROM ${tableName} p WHERE p.is_delete = 0 AND p.group_id = t.group_id ORDER BY RAND() LIMIT 1 ) AS picUrl FROM ${tableName} t WHERE t.is_delete = 0
AND t.group_id = #{qry.groupId}
AND t.pic_name LIKE CONCAT('%', #{qry.picName}, '%')
GROUP BY t.group_id, t.pic_name ORDER BY RAND() LIMIT #{limit}
